mapper.update:
UPDATE animal
SET readcnt = readcnt + 1
WHERE animal_id = #{animalId}

mapper.select:
SELECT NVL((
			SELECT 1 FROM animal
			WHERE animal_id = #{animalId} AND animal_passwd = #{animalPasswd}
			), 0) as state
FROM dual

mapper.update:
UPDATE animal SET animal_name = #{animalName}, animal_species = #{animalSpecies}, animal_gender = #{animalGender}, animal_kg = #{animalKg}, animal_age = #{animalAge}, animal_color = #{animalColor}, animal_status = #{animalStatus}, animal_memo = #{animalMemo}, animal_temp = #{animalTemp}, animal_regist = sysdate
, animal_passwd = #{animalPasswd}
, animal_file = #{animalFile}
WHERE animal_id = #{animalId}
